<?php/*Template Name: Seri Filmler*/get_header();?><div id="content">	<div id="boxet" class="incontent">		<div class="title">			<h1 class="title-border bd-purple"><i class="fas fa-play-circle"></i> <?php the_title(); ?></h1>		</div>		<div id="listehizala">		<?php 		$total = array(					'parent' => 0,					'hide_empty' => 0,					'name__like'  => 'Serisi',				);		$total_categories = get_categories( $total );		$total_count =  ceil(count( $total_categories )/20);  		$paged = (get_query_var('paged')) ? get_query_var('paged') : 1;		$posts_per_page = 20;		$offset = ($posts_per_page * $paged) - 20 ;		$args = array(			'orderby' => 'name',			'order' => 'ASC',			'parent' => 0,			'hide_empty' => 0,			'name__like'  => 'Serisi',			'number' => $posts_per_page,			'offset' => $offset,			'posts_per_page' => 20,			'paged' => $paged,			);		$categories = get_categories( $args );		foreach ( $categories as $category ) {			$category_id = get_cat_ID( '' . $category->name . '' );			$category_link = get_category_link( $category_id );			?>			<div class="listmovie">					<div class="movie-box">					<a href="<?php echo esc_url( $category_link );?>">						<div class="poster">												<div class="img">							<?php							if ( get_term_meta($category->term_id, 'resim_url', true) ) {								echo '<img width="300px" height="450px" class="lazy" data-src="'.get_term_meta($category->term_id, 'resim_url', true).'" alt="'.get_the_title().'" />'; 							} else {								echo '<img width="300px" height="450px" class="lazy" data-src="'.get_template_directory_uri().'/images/no-thumbnail.png" alt="'.get_the_title().'" />';							}							?>							</div>						</div>					</a>					<div class="bolum-alt">						<div class="film-ismi">							<a href="<?php echo esc_url( $category_link );?>"><?php echo $category->name;?></a>						</div>					</div>				</div>			</div>			<?php		}	?>	</div>	<?php	$big = 999999999;	echo '<div class="paginate-links">';	echo paginate_links( array(		'base' => str_replace( $big, '%#%', esc_url( get_pagenum_link( $big ) ) ),		'format' => '?paged=%#%',		'prev_text' => __('«'),		'next_text' => __('»'),		'current' => max( 1, get_query_var('paged') ),		'total' => $total_count		) );	echo '</div>';	?>	</div>	<?php get_sidebar();?></div><?php get_footer();?>